    16‚Üí
    17‚Üí  // Inicializar navegaci√≥n desplegable
    18‚Üí  initializeDropdownNavigation();
    19‚Üí
    20‚Üí  const links = Array.from(document.querySelectorAll('.nav a'));
    21‚Üí  const sections = links
    22‚Üí    .map(a => document.querySelector(a.getAttribute('href')))
    23‚Üí    .filter(Boolean);
    24‚Üí  const totalPages = sections.length;
    25‚Üí
    26‚Üí  // Smooth scroll
    27‚Üí  links.forEach(link => {
    28‚Üí    link.addEventListener('click', (e) => {
    29‚Üí      e.preventDefault();
    30‚Üí      const id = link.getAttribute('href');
    31‚Üí      const target = document.querySelector(id);
    32‚Üí      if (target) target.scrollIntoView({ behavior: 'smooth', block: 'start' });
    33‚Üí    });
    34‚Üí  });
    35‚Üí
    36‚Üí  // Active link on scroll
    37‚Üí  const observer = new IntersectionObserver(entries => {
    38‚Üí    entries.forEach(entry => {
    39‚Üí      const id = '#' + entry.target.id;
    40‚Üí      const link = links.find(a => a.getAttribute('href') === id);
    41‚Üí      if (entry.isIntersecting) {
    42‚Üí        links.forEach(a => a.classList.remove('active'));
    43‚Üí        if (link) link.classList.add('active');
    44‚Üí      }
    45‚Üí    });
    46‚Üí  }, { rootMargin: '-30% 0px -60% 0px', threshold: 0.1 });
    47‚Üí
    48‚Üí  sections.forEach(sec => observer.observe(sec));
    49‚Üí
    50‚Üí  // Toggle sidebar (modo compacto)
    51‚Üí  const toggle = document.querySelector('.toggle');
    52‚Üí  const sidebar = document.querySelector('.sidebar');
    53‚Üí  if (toggle && sidebar) {
