    58‚Üí  }
    59‚Üí
    60‚Üí  // Funci√≥n para inicializar la navegaci√≥n desplegable
    61‚Üí  function initializeDropdownNavigation() {
    62‚Üí    const sectionToggles = document.querySelectorAll('.section-toggle');
    63‚Üí    
    64‚Üí    sectionToggles.forEach(toggle => {
