   104‚Üí  // Cargar JSON con datos reales de la auditor√≠a
   105‚Üí  const tryLoad = (url) => fetch(url).then(r => r.ok ? r.json() : Promise.reject('no_json'));
   106‚Üí  tryLoad('../../../analisis_pdf_auditoria/analisis_estructura.json')
   107‚Üí    .catch(() => tryLoad('data/analisis_estructura.json'))
   108‚Üí    .then(data => {
   109‚Üí      // Espera estructura: { paginas: [{ id: 'pagina-001', titulo: '...', subtitulo: '...', bullets: [...] }, ...] }
   110‚Üí      if (!data || !Array.isArray(data.paginas)) return;
   111‚Üí
   112‚Üí      data.paginas.forEach((p, index) => {
