   151‚Üí          right.textContent = `P√°gina ${pageIndex} de ${totalPages}`;
   152‚Üí
   153‚Üí          if (!footer.contains(left)) footer.insertBefore(left, footer.firstChild);
   154‚Üí        }
   155‚Üí      });
   156‚Üí
   157‚Üí      // Crear navegaci√≥n jer√°rquica con secciones principales
   158‚Üí      const nav = document.querySelector('.nav ul');
   159‚Üí      if (nav && data.secciones) {
