   194‚Üí          // Agregar p√°ginas de la secci√≥n
   195‚Üí          seccion.paginas.forEach(pageNum => {
   196‚Üí            const page = data.paginas.find(p => p.id === `pagina-${pageNum.toString().padStart(3, '0')}`);
