   255‚Üí      logAction('json_loaded', { paginas: data.paginas.length });
   256‚Üí    })
   257‚Üí    .catch(() => {
   258‚Üí      // Fallback sin JSON: formatear footers con t√≠tulo actual y fuentes existentes
   259‚Üí      sections.forEach((sec, idx) => {
